import Listen from './components/Listen'
import User from './components/User'
import Room from './components/Room'
import SpotifyPlayer from './components/SpotifyPlayer'
import Button from 'react-bootstrap/Button'
import { useState, useEffect } from 'react'
import { Spotify } from 'react-spotify-embed'
import './index.css'


function App() {
    const [user, setUser] = useState('')
    const [getUser, setGetUser] = useState(true)
    const [listening, setListen] = useState(false) 
    const [room, setRoom] = useState('')
    const [key, setKey] = useState('')
    const [auth, setAuth] = useState(false)

    const onSubmit = async (e, name) => {
        e.preventDefault()
        setUser(name)

        try {
            const res = await fetch('http://localhost:3001/listen', {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name: name
                })
            })   

            const data = await res.json()
            if (data.message === 'Success') {
                setAuth(true)
            } else {
                alert('Please authenticate to use this application.')
            }

        } catch (error) {
            console.log('Unable to start listening: ' + error)
        }

        setGetUser(false)
    }

    const onListen = (sessionKey, roomName) => {
        setKey(sessionKey)
        setRoom(roomName)
        console.log("Listening in room: " + roomName + sessionKey)
        setListen(true)

    }

    useEffect(() => {
        const hash = window.location.hash // look for hash in url
        let token = window.localStorage.getItem("token") // look for token in local storage

        if (!token && hash) { // if no token but here is hash in url
            token = hash.substring(1).split('&').find(elem => elem.startsWith('access_token')).split('=')[1] // parse hash

            console.log(window.location.hash)
            window.location.hash = '' // remove hash from url
            window.localStorage.setItem('token', token) // set token into local storage
        }

        setToken(token)
    }, [])

    // const openAuth = async () => {
    //     try {
    //         console.log('login');
    //         const response = await fetch('http://localhost:3001/login')
    //         const data = await response.json()
    //         console.log(data)

    //         // window.location.href = data
    //     } catch (error) {
    //         console.log('Unable to authenticate Spotify: ' + error);
    //     }
    // }

    const logout = () => {
        setToken('') 
        window.localStorage.removeItem('token') // remove token from local storage
    }

    return (
        <div className="container">
            {/* <div className="nav">
                {!token ?
                    <Button onClick={openAuth}>Login</Button> :
                    <Button className='logout' onClick={logout}>Logout</Button>
                }
            </div> */}

            <div className="content-body">
                {(!listening) ?
                    (getUser ? <User onSubmit={onSubmit}/> : <Listen user={user} onListen={onListen}/>) :
                    null
                }       

                {(listening && key) && <Room roomKey={key} name={room}/>}   
                {/* <Room roomKey={key} name={room} /> */}
            </div>

        </div>

    );
}

export default App
