import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import "bootstrap/dist/css/bootstrap.min.css"
import { useState, useEffect } from 'react'

const Listen = () => {
    const [listenOpt, setListen] = useState('')
    const [key, setKey] = useState('')
    const [joiningRoom, setJoinRoom] = useState(false)
    const [show, setShow] = useState(false)

    const joinRoom = () => {
        console.log("joining room...");
        setJoinRoom(true)
    }

    const enterKeyForm = () => {
        return (
            <Form style="width: 100%;">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>

                <Button variant="primary" type="submit">
                Submit
                </Button>
            </Form>
        )

    }

    const createRoom = () => {
        console.log("create room...");
        // handleClose()
    }

    const handleClick = (event) => {
        setListen(event.target.getAttribute('data-value'))
        handleOpen() // show modal
    }

    const handleClose = () => setShow(false)
    const handleOpen = () => setShow(true)

    return (
        <>
            <h1>I want to listen</h1>
            <div className="btn-container">
                <Button size="lg" data-value="alone" onClick={handleClick}>alone</Button>
                <Button size="lg" data-value="friend" onClick={handleClick}>with a friend</Button>
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Button className="key-btn" size="lg" onClick={joinRoom}>Join room</Button>
                    <Button className="key-btn" size="lg" onClick={createRoom} id="make">Make room</Button>
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: 'flex-start' }}>
                    {joiningRoom && enterKeyForm()}
                </Modal.Footer>
            </Modal>
        </>
    )
}

export default Listen
