import Listen from './components/Listen'
import User from './components/User'
import Room from './components/Room'
import SpotifyPlayer from './components/SpotifyPlayer'
import Button from 'react-bootstrap/Button'
import { useState, useEffect } from 'react'
import { Spotify } from 'react-spotify-embed'



function App() {
    const CLIENT_ID = 'b237d6593cfb45da94a3e834f4c58f7b'
    const REDIRECT_URI = 'http://localhost:3000/callback'
    const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize'
    const RESPONSE_TYPE = 'token'

    const [user, setUser] = useState('')
    const [getUser, setGetUser] = useState(true)
    const [listening, setListen] = useState(false) 
    const [room, setRoom] = useState('')
    const [token, setToken] = useState('')

    const onSubmit = (e, name) => {
        e.preventDefault()
        setUser(name)
        setGetUser(false)
    }

    const onListen = (sessionKey) => {
        setRoom(sessionKey)
        setListen(true)
    }

    useEffect(() => {
        const hash = window.location.hash // look for hash in url
        let token = window.localStorage.getItem("token") // look for token in local storage

        if (!token && hash) { // if no token but here is hash in url
            token = hash.substring(1).split('&').find(elem => elem.startsWith('access_token')).split('=')[1] // parse hash

            window.location.hash = '' // remove hash from url
            window.localStorage.setItem('token', token) // set token into local storage
        }

        setToken(token)
    }, [])

    const openAuth = () => {
        window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`
    }

    const logout = () => {
        setToken('') 
        window.localStorage.removeItem('token') // remove token from local storage
    }

    return (
        <div className="container">
            {!token ?
                <Button onClick={openAuth}>Login</Button> :
                <Button onClick={logout}>Logout</Button>
            }

            {(token && !listening) ?
                (getUser ? <User onSubmit={onSubmit}/> : <Listen user={user} onListen={onListen}/>) :
                null

                // <h1>you have joined the room</h1>
            }

            {/* {!listening && token ? 
               (getUser ? <User onSubmit={onSubmit}/> : <Listen user={user} onListen={onListen}/>) :
                <h1>you have joined the room</h1>
            } */}

            {/* {listening && <Room sessionKey={room}/>}    

            <SpotifyPlayer /> */}

        </div>

    );
}

export default App
