import { useState, useRef } from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import KeyContainer from './KeyContainer'
import '../index.css';

const Key = ({ user, newRoom }) => {
    const [key, setKey] = useState('')
    const [room, setRoom] = useState('')
    const [copy, setCopy] = useState(false)
    const keyRef = useRef()

    const keyDivStyle = {
        display: "flex",
        justifyContent: "space-between",
        width: "100%",
    }

    const getKey = async (e) => {
        // get room name
        if (room === '') {
            alert('Please enter a room name')
            return
        } 

        // get a new key from server
        const newSession = await fetch('http://localhost:3001/key/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                sessionUser: [user],
                sessionName: room 
            })
        })

        const newKey = await newSession.json()
        console.log(newKey)
        setKey(newKey.sessionKey) // set the new key
        newRoom(newKey.sessionKey, newKey.sessionName) // pass key back to parent
    }

    const onInput = (e) => {
        setRoom(e.target.value)
    }

    return (
        <>
            {!key ? 
                <Form style={{ width: "100%" }}> 
                    <Form.Label>Name:</Form.Label>
                    <Form.Control type="text" onChange={onInput} value={room} />
                    <Button variant="primary" type="submit" onClick={getKey}>
                        Submit
                    </Button>
                </Form> :
                <>
                    <KeyContainer key={key}/>
                    {/* <p>Key:</p>
                    <div className="new-key-container"></div>
                        <div className="new-key-container" style={keyDivStyle}>
                        <div className="key-container">
                            <p ref={keyRef}>{key}</p>
                        </div>
                        <CopyButton onClick={() => copyToClipboard()} showCopied={copy} />
                    </div> */}
                </>
            } 
        </>
    )
}

export default Key
