import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import "bootstrap/dist/css/bootstrap.min.css"
import { useState, useEffect } from 'react'
import KeyForm from './KeyForm'
import Key from './Key'
import io from 'socket.io-client'

const socket = io.connect('http://localhost:3001') // url from backend server

const Listen = () => {
    const [listenOpt, setListen] = useState('')
    const [message, setMessage] = useState('')
    const [msgReceived, setMsgRcvd] = useState('')
    const [keyContainer, setKeyContainer] = useState(false)
    const [sessionKey, setKey] = useState('')
    const [keyForm, showKeyForm] = useState(false)
    const [show, setShow] = useState(false)

    const joinRoom = () => {
        if (keyContainer) { // if key container exists
            setKeyContainer(false) // remove key container
        }

        if (!keyForm) { // if it's not already showing then show it
            showKeyForm(true)
        }

        // if (key !== '') {
        //     socket.emit('join_room', {message: "Hello"})
        // }
    }

    const makeRoom = async () => {
        if (keyForm) { // if key form from join room exists
            showKeyForm(false) // remove key form
        }

        if (!keyContainer) { // if key not showing show it
            setKeyContainer(true)

            // get a new key from server
            const newKey = await fetch('http://localhost:3001/key/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    sessionUser: "billy", 
                })
            })

            const key = await newKey.json()
            console.log(key)

            setKey(key) // get newly generated key
        }
    }

    const handleClick = (event) => {
        setListen(event.target.getAttribute('data-value'))
        handleOpen() // show modal
    }

    const handleClose = () => setShow(false)
    const handleOpen = () => setShow(true)

    const sendMessage = () => {
        socket.emit("send_message", {message: "Hello"}) // emit to back end 
    }

    useEffect(() => {
        socket.on('receive_message', (data) => {
            setMsgRcvd(data.message)
        })
    }, [socket, sessionKey])


    return (
        <>
            <h1>I want to listen</h1>
            <div className="btn-container">
                <Button size="lg" data-value="alone" onClick={sendMessage}>alone</Button>
                <Button size="lg" data-value="friend" onClick={handleClick}>with a friend</Button>
            </div>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Button className="key-btn" size="lg" onClick={joinRoom}>Join room</Button>
                    <Button className="key-btn" size="lg" onClick={makeRoom} id="make">Make room</Button>
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: 'flex-start' }}>
                    {keyForm && <KeyForm />}
                    {keyContainer && <Key key={sessionKey}/>}
                </Modal.Footer>
            </Modal>
            {/* <input onChange={(event) => {
                setMessage(event.target.value)
            }}
            />
            <h1>Message</h1>
            {msgReceived} */}
        </>
    )
}

export default Listen
