import { useState, useRef } from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import '../index.css';
import CopyButton from './CopyButton';

const Key = ({ user }) => {
    const [key, setKey] = useState('')
    const [copy, setCopy] = useState(false)
    const keyRef = useRef()

    const keyDivStyle = {
        display: "flex",
        justifyContent: "space-between",
        width: "100%",
    };

    const copyToClipboard = () => {
        const key = keyRef.current.innerText // Get the text content of the paragraph
        navigator.clipboard.writeText(key) // Use Clipboard API to copy text
            .then(() => {
                console.log('Text copied to clipboard')
                console.log(key)
            })
            .catch(err => {
                console.error('Failed to copy text: ', err)
            })
    }

    const makeRoom = (e) => {
        e.preventDefault()
        let roomName = e.target.value
        createRoom(roomName)
    }

    return (
        <>
            {!key ? 
                <Form style={{ width: "100%" }}> 
                    <Form.Label>Name:</Form.Label>
                    <Form.Control type="text" />
                    <Button variant="primary" type="submit" onClick={makeRoom}>
                        Submit
                    </Button>
                </Form> :
                <>
                    <p>Key:</p>
                    <div className="new-key-container"></div>
