import { Button } from 'react-bootstrap'
import "bootstrap/dist/css/bootstrap.min.css"
import { useState, useEffect } from 'react'

const Listen = () => {
    const [listenOpt, setListen] = useState('')

    useEffect(() => {
        console.log("Listening option updated: " + listenOpt);
    }, [listenOpt])

    return (
        <>
            <h1>I want to listen</h1>
            <div class="btn-container">
                <Button size="lg" data-value="alone" onClick={setListen}>alone</Button>
                <Button size="lg" data-value="friend" onClick={setListen}>with a friend</Button>
            </div>
        </>
    )
}

export default Listen
