import Listen from './components/Listen'
import User from './components/User'
import Room from './components/Room'
import Button from 'react-bootstrap/Button'
import { useState, useEffect } from 'react'
import { WebPlaybackSDK } from 'react-spotify-web-playback-sdk'


function App() {
    const CLIENT_ID = 'b237d6593cfb45da94a3e834f4c58f7b'
    const REDIRECT_URI = 'http://localhost:3000/callback'
    const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize'
    const RESPONSE_TYPE = 'token'

    const [user, setUser] = useState('')
    const [getUser, setGetUser] = useState(true)
    const [listening, setListen] = useState(false) 
    const [room, setRoom] = useState('')
    const [token, setToken] = useState('')

    const onSubmit = (e, name) => {
        e.preventDefault()
        setUser(name)
        setGetUser(false)
    }

    const onListen = (sessionKey) => {
        setRoom(sessionKey)
        setListen(true)
    }

    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")

        if (!token && hash) {
            token = hash.substring(1).split('&').find(elem => elem.startsWith('access_token')).split('=')[1]

            window.location.hash = ''
            window.localStorage.setItem('token', token)
        }

        setToken(token)

    }, [])

    const logout = () => {
        setToken('')
        window.localStorage.removeItem('token')
    }

    // const getOAuthToken= useCallback(callback => callback(token), [])

    return (
        <div className="container">
            {!token ?
                <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login</a> :
                <Button onClick={logout}>Logout</Button>
            }

            {!listening ? 
               (getUser ? <User onSubmit={onSubmit}/> : <Listen user={user} onListen={onListen}/>) :
                <h1>you have joined the room</h1>
            }

            {listening && <Room sessionKey={room}/>}    

            {token &&
                <WebPlaybackSDK
                    deviceName="My awesome Spotify app"
                    getOAuthToken={token}
                    volume={0.5}
                    connectOnInitialized={true}>
                </WebPlaybackSDK>
            }
        </div>

    );
}

export default App
